package com.kingdangkou.weixin.weixiaodan.filter;import javax.servlet.*;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * Created by apple on 17/1/3. */public class ResponseFilter implements Filter {    private boolean isDebug;    @Override    public void init(FilterConfig filterConfig) throws ServletException {        isDebug = Boolean.valueOf(filterConfig.getInitParameter("isDebug"));    }    @Override    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {        filterChain.doFilter(servletRequest, servletResponse);        HttpServletResponse response = (HttpServletResponse) servletResponse;        response.setHeader("Access-Control-Allow-Origin","*");        response.setHeader("Access-Control-Allow-Methods","GET,POST");    }    @Override    public void destroy() {    }}