package com.kingdangkou.weixin.weixiaodan.filter;import javax.servlet.*;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.io.PrintWriter;/** * Created by apple on 17/1/3. */public class ResponseFilter implements Filter {    private boolean isDebug;    @Override    public void init(FilterConfig filterConfig) throws ServletException {        isDebug = Boolean.valueOf(filterConfig.getInitParameter("isDebug"));    }    @Override    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {        if (isDebug){            HttpServletResponse response = (HttpServletResponse) servletResponse;            HttpCharacterResponseWrapper ref_charResponse = new HttpCharacterResponseWrapper(response);            ref_charResponse.setHeader("Access-Control-Allow-Origin","*");            ref_charResponse.setHeader("Access-Control-Allow-Methods","GET,POST");            filterChain.doFilter(servletRequest, ref_charResponse);            String output = ref_charResponse.getRef_charArrayWriter().toString();            output = "callback(" + output + ")";            PrintWriter writer = response.getWriter();            writer.write(output);        }else {            filterChain.doFilter(servletRequest, servletResponse);        }    }    @Override    public void destroy() {    }}